mapper.insert:

insert into BUILDING_NO (ID, NAME, DESCRIPT, 
  ESTATE_ID, MONTH_PAY,building_Id, REMARK
  )
values (#{id,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{descript,jdbcType=VARCHAR}, 
  #{estateId,jdbcType=DECIMAL}, #{monthPay,jdbcType=DECIMAL},#{buildingId,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}
  )

mapper.select:

select 
 count(*)
from BUILDING_NO where estate_id=#{0}

select  

 from BUILDING_NO where building_id=#{0}  and estate_id=#{1}
 order by cast(substr(name, 1, length(name)-2) as number)

    select 

		    FROM 
			( 
		    	SELECT A.*, ROWNUM RN 
				FROM (
		       		SELECT * FROM building_no where estate_id=#{0}
		       		order by cast(substr(name, 1, length(name)-2) as number)

		     		  ) A
			) WHERE RN BETWEEN #{1} AND #{2}
