mapper.select:

where rep.isDelete = 'false'

	and e.name = #{tenement}


	and rep.third_com = #{thirdCom}


where rep.isDelete = 'false'

and rep.masterID = #{masterID}

order by rep.repairer_time desc


where rep.isDelete = 'false'

and e.name = #{tenement}


and h.room_no = #{roomNum}


and substr(n.name, length(n.name )-2,1) = #{building}


and n.building_id =#{floor}


and rep.repairer like '%'||#{repairer}||'%' 



and rep.id = #{id}


and rep.repairer_time >= #{repairerTime}


and rep.start_time >= #{startTime}


and rep.end_time <= #{endTime}


and rep.status = #{status}


and rep.third_com = #{thirdCom}

mapper.update:
update repaire set
  checker = #{checker}
where id = #{id}

update repaire set
  status = #{status}
where id = #{id}

update repaire set

		status = #{status},

	validate_time = #{validateTime}
where id = #{id}

mapper.insert:
INSERT INTO repaire( id, repairer, repairer_phone, repairer_time, repaire_type, comments, floor, status, masterID, houseID
) values ( #{id}, #{repairer}, #{repairerPhone}, #{repairerTime}, #{repaireType}, #{comments}, #{floor}, '已登记', #{masterID}, #{houseID}
)

mapper.update:
